  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
  <option></option>
</select>
<input type="text" alt="Адресс" style={{width:'250px'}}/>
<h3 className='white' >Ваш адресс:</h3>
<input type="text" alt="Адресс" style={{width:'250px'}}/>
</div>
<br/>
<div className="order-buttons">
<input type="submit" id ="submit"value="Заказать" />
<input type="button" id ="cancel"value="Отменить" onClick={props.hideOrderBody}/>
</div>
</form>

</div>

        </div>

        </div>
    )
}
